INSERT INTO `email_templates` VALUES (1,'User Create','user-create','User Create','\r\n                    Hello, {{name}}\r\n\r\n                    Thank you for creating an account with us. Please verify your email address to activate your account by clicking the link below:\r\n\r\n                    Verification Link: {{link}}\r\n\r\n                    Your account details:\r\n                    Email: {{email}}\r\n                    Temporary Password: {{password}}\r\n\r\n                    If you did not register with us, please ignore this email.\r\n\r\n                    Best regards.\r\n                ',1,'{{name}}, {{email}}, {{link}}, {{password}}',5,NULL,'2024-12-03 00:09:37','2024-12-03 00:09:37'),(2,'Password Reset','password-reset','Password Reset','\r\n                    Hello, {{name}}\r\n\r\n                    We received a request to reset the password associated with your account ({{email}}). Please click the link below to reset your password:\r\n\r\n                    Reset Password Link: {{link}}\r\n\r\n                    If you did not make this request, please contact our support team immediately.\r\n\r\n                    Best regards.\r\n                ',1,'{{name}}, {{email}}, {{link}}',5,NULL,'2024-12-03 00:09:37','2024-12-03 00:09:37'),(3,'Verify','verify','Verify','\r\n                     Hello, {{name}}\r\n\r\n                    To complete the registration process, please verify your email address by clicking the link below:\r\n\r\n                    Verification Link: {{link}}\r\n\r\n                    If you did not sign up for this account using {{email}}, please disregard this message.\r\n\r\n                    Best regards.\r\n                ',1,'{{name}}, {{email}}, {{link}}',5,NULL,'2024-12-03 00:09:37','2024-12-03 00:09:37'),(4,'Consultation Booking Success','consultation-booking-success','Consultation Booking Success','\r\n                    Hello, {{name}}\r\n\r\n                    Your consultation has 