INSERT INTO `testimonials` (id, name, description, image, status, created_at, updated_at, deleted_at) VALUES (1,'Benjamin Harris','Studylifter made my dream of studying abroad come true. Their team handled everything smoothly, from university selection to visa application.','66',1,'2024-11-03 05:01:51','2024-11-25 04:51:46',NULL),(2,'Christopher Jordan','I felt overwhelmed by the application process, but Studylifter guided me every step of the way. Highly recommend their services!','67',1,'2024-11-03 05:03:27','2024-11-03 05:03:27',NULL),(3,'Ava White','Their expertise in securing scholarships is incredible. I saved a lot of money and had the best study experience.','68',1,'2024-11-03 05:03:50','2024-11-25 04:51:37',NULL),(4,'Emma Thomas','Studylifters team was always available to answer my questions. They made my journey stress-free.','69',1,'2024-11-03 05:04:23','2024-11-25 04:51:24',NULL),(5,'Emily Johnson','Studylifters guidance and support were invaluable. Their team went above and beyond to ensure I felt confident and informed every step of the way.','135',1,'2024-11-03 05:04:23','2024-11-25 04:58:24',NULL),(6,'Jhon Moragn','Working with Studylifter was a fantastic experience. Their team was proactive, knowledgeable, and truly dedicated to helping me succeed.','136',1,'2024-11-03 05:04:23','2024-11-25 04:58:43',NULL);
